// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faCamera } from "@fortawesome/free-solid-svg-icons";
import styles from "./Element.module.css";

function Element({runtime, coverImg,likeCount,descriptionIntro,title,originalImg}){

    return(
        <div>
            <img className={styles.movie__originalImg} src={originalImg} alt={title}/>
            <div className={styles.movie}>
                <div>
                    <img className={styles.movie__img} src={coverImg} alt={title}/>
                    <span className={styles.movie__title}>{title}</span>
                    <span className={styles.movie__runTime}>Runtime: {String(Math.floor(runtime/60)).padStart(2,"0")}h {String(runtime%60).padStart(2,"0")}m </span>
                    <span className={styles.movie__like}>Like : {likeCount}</span>
                </div>
                    <div className={styles.movie__description} >
                        <span>Description</span>
                        <p className={styles.movie__descriptionIntro}>{descriptionIntro}</p>
                    </div>
            </div>
            
        </div>
    )
    } 
    export default Element;
